                float t = Mathf.Clamp01(_elapsedTime / _deafDuration);
                float recovery = 1f - Mathf.Pow(1f - t, _recoverySlope); // Logistic-ish curve
                _deafnessLevel = Mathf.Lerp(_targetDeafness, 0f, recovery);
