            string id = bot.Profile.Id;
            Player player = bot.GetPlayer;

            lock (Lock)
            {
                CacheMap.Remove(id);
                if (player != null)
                    PlayerMap.Remove(player);

                Logger.LogDebug($"[BotComponentCacheRegistry] Removed cache for: {id}");
            }
        }

        public static void ClearAll()
