                Vector3 origin = _bot.Position + Vector3.up * 0.2f;
                Vector3 forward = _bot.Transform.forward;
                Vector3 right = _bot.Transform.right;

                int rays = Mathf.CeilToInt((EdgeCheckDistance * 2f) / EdgeRaySpacing);

                for (int i = 0; i < rays; i++)
