        private void PauseMovement(float time)
        {
            try
            {
                float duration = BasePauseDuration * Mathf.Clamp(0.5f + _profile.Caution, 0.35f, 2.0f);
                _bot.Mover.MovementPause(duration);
                _pauseUntil = time + duration;
            }
            catch (Exception ex)
