                            float fallHeight = Mathf.Abs(_context.TransformPosition.y - landHits[0].point.y);
                            if (fallHeight > SafeFallHeight)
                                return false;

                            target = landHits[0].point;
                            return true;
                        }
                        finally
                        {
                            TempRaycastHitPool.Return(landHits);
                        }
                    }
                    finally
