				ClearExpiredCache();

				string squadId = _bot.Profile?.Info?.GroupId ?? _bot.ProfileId;
				if (!_squadRetreatCache.TryGetValue(map, out var squadCache))
				{
					squadCache = new Dictionary<string, List<Vector3>>();
					_squadRetreatCache[map] = squadCache;
